import React, { useState, useEffect } from 'react';
import api from '../utils/axios.config';
import '../styles/CustomerDashboard.css';

const CustomerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    gender: 'all',
    search: '',
    sortBy: 'none'
  });

  // Fetch products
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await api.get('/products');
        setProducts(response.data);
        setFilteredProducts(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching products');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Apply filters and sorting
  useEffect(() => {
    let result = [...products];

    // Apply gender filter
    if (filters.gender !== 'all') {
      result = result.filter(product => product.category === filters.gender);
    }

    // Apply search filter
    if (filters.search) {
      result = result.filter(product =>
        product.name.toLowerCase().includes(filters.search.toLowerCase()) ||
        product.material.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply sorting
    if (filters.sortBy === 'price-asc') {
      result.sort((a, b) => a.unit_price - b.unit_price);
    } else if (filters.sortBy === 'price-desc') {
      result.sort((a, b) => b.unit_price - a.unit_price);
    }

    setFilteredProducts(result);
  }, [filters, products]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="customer-dashboard">
      <div className="dashboard-header">
        <h1>Welcome to Textile Store</h1>
        <p>Discover our collection of quality textiles</p>
      </div>

      <div className="filters-container">
        <h2>Filter Products</h2>
        <div className="filters-section">
          {/* Search Bar */}
          <div className="search-bar">
            <input
              type="text"
              name="search"
              placeholder="Search products..."
              value={filters.search}
              onChange={handleFilterChange}
              className="search-input"
            />
          </div>

          {/* Gender Filter */}
          <div className="filter-group">
            <label>Category:</label>
            <select
              name="gender"
              value={filters.gender}
              onChange={handleFilterChange}
              className="filter-select"
            >
              <option value="all">All Categories</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </div>

          {/* Sort Filter */}
          <div className="filter-group">
            <label>Sort by:</label>
            <select
              name="sortBy"
              value={filters.sortBy}
              onChange={handleFilterChange}
              className="filter-select"
            >
              <option value="none">Default</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
            </select>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="loading-spinner">Loading...</div>
      ) : error ? (
        <div className="error-message">{error}</div>
      ) : (
        <div className="products-section">
          <h2>Available Products {filters.gender !== 'all' && `- ${filters.gender.charAt(0).toUpperCase() + filters.gender.slice(1)}`}</h2>
          <div className="products-count">
            Showing {filteredProducts.length} products
          </div>
          <div className="products-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <div className="product-category-tag">
                  {product.category}
                </div>
                <div className="product-image-placeholder">
                  {/* Add actual image here when available */}
                  <div className="placeholder-text">Product Image</div>
                </div>
                <div className="product-info">
                  <h3 className="product-title">{product.name}</h3>
                  <div className="product-details">
                    <p className="material">
                      <span>Material:</span> {product.material}
                    </p>
                    <p className="stock">
                      <span>In Stock:</span> {product.aval_quantity}
                    </p>
                    <p className="price">â‚¹{product.unit_price.toFixed(2)}</p>
                  </div>
                  <button className="add-to-cart-btn">Add to Cart</button>
                </div>
              </div>
            ))}
          </div>

          {filteredProducts.length === 0 && (
            <div className="no-products">
              <h3>No products found</h3>
              <p>Try adjusting your filters or search terms</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CustomerDashboard;
